    public function parse_ws_trade($trade, $market = null) {
        //
        //     {
        //         "symbol":"SPOT_ADA_USDT",
        //         "timestamp":1618820361552,
        //         "price":1.27988,
        //         "size":300,
        //         "side":"BUY",
        //         "source":0
        //     }
        // private $trade
        //    {
        //     "msgType" => 0,  // execution report
        //     "symbol" => "SPOT_BTC_USDT",
        //     "clientOrderId" => 0,
        //     "orderId" => 54774393,
        //     "type" => "MARKET",
        //     "side" => "BUY",
        //     "quantity" => 0.0,
        //     "price" => 0.0,
        //     "tradeId" => 56201985,
        //     "executedPrice" => 23534.06,
        //     "executedQuantity" => 0.00040791,
        //     "fee" => 2.1E-7,
        //     "feeAsset" => "BTC",
        //     "totalExecutedQuantity" => 0.00040791,
        //     "avgPrice" => 23534.06,
        //     "status" => "FILLED",
        //     "reason" => "",
        //     "orderTag" => "default",
        //     "totalFee" => 2.1E-7,
        //     "feeCurrency" => "BTC",
        //     "totalRebate" => 0,
        //     "rebateCurrency" => "USDT",
        //     "visible" => 0.0,
        //     "timestamp" => 1675406261689,
        //     "reduceOnly" => false,
        //     "maker" => false
        //   }
        //
        $marketId = $this->safe_string($trade, 'symbol');
        $market = $this->safe_market($marketId, $market);
        $symbol = $market['symbol'];
        $price = $this->safe_string($trade, 'executedPrice', 'price');
        $amount = $this->safe_string_2($trade, 'executedQuantity', 'size');
        $cost = Precise::string_mul($price, $amount);
        $side = $this->safe_string_lower($trade, 'side');
        $timestamp = $this->safe_integer($trade, 'timestamp');
        $maker = $this->safe_bool($trade, 'marker');
        $takerOrMaker = null;
        if ($maker !== null) {
            $takerOrMaker = $maker ? 'maker' : 'taker';
        }
        $type = $this->safe_string_lower($trade, 'type');
        $fee = null;
        $feeCost = $this->safe_number($trade, 'fee');
        if ($feeCost !== null) {
            $fee = array(
                'cost' => $feeCost,
                'currency' => $this->safe_currency_code($this->safe_string($trade, 'feeCurrency')),
            );
        }
        return $this->safe_trade(array(
            'id' => $this->safe_string($trade, 'tradeId'),
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'side' => $side,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'order' => $this->safe_string($trade, 'orderId'),
            'takerOrMaker' => $takerOrMaker,
            'type' => $type,
            'fee' => $fee,
            'info' => $trade,
        ), $market);
    }

    public function check_required_uid($error = true) {
        if (!$this->uid) {
            if ($error) {
                throw new AuthenticationError($this->id . ' requires `uid` credential');
            } else {
                return false;
            }
        }
        return true;
    }

    public function authenticate($params = array ()) {
        return Async\async(function () use ($params) {
            $this->check_required_credentials();
            $url = $this->urls['api']['ws']['private'] . '/' . $this->uid;
            $client = $this->client($url);
            $messageHash = 'authenticated';
            $event = 'auth';
            $future = $client->future ($messageHash);
            $authenticated = $this->safe_value($client->subscriptions, $messageHash);
            if ($authenticated === null) {
                $ts = (string) $this->nonce();
                $auth = '|' . $ts;
                $signature = $this->hmac($this->encode($auth), $this->encode($this->secret), 'sha256');
                $request = array(
                    'event' => $event,
                    'params' => array(
                        'apikey' => $this->apiKey,
                        'sign' => $signature,
                        'timestamp' => $ts,
                    ),
                );
                $message = array_merge($request, $params);
                $this->watch($url, $messageHash, $message, $messageHash);
            }
            return Async\await($future);
        }) ();
    }

    public function watch_private($messageHash, $message, $params = array ()) {
        return Async\async(function () use ($messageHash, $message, $params) {
            Async\await($this->authenticate($params));
            $url = $this->urls['api']['ws']['private'] . '/' . $this->uid;
            $requestId = $this->request_id($url);
            $subscribe = array(
                'id' => $requestId,
            );
            $request = array_merge($subscribe, $message);
            return Async\await($this->watch($url, $messageHash, $request, $messageHash, $subscribe));
        }) ();
    }

    public function watch_orders(?string $symbol = null, ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * @see https://docs.woo.org/#executionreport
             * @see https://docs.woo.org/#algoexecutionreportv2
             * watches information on multiple $orders made by the user
             * @param {string} $symbol unified $market $symbol of the $market $orders were made in
             * @param {int} [$since] the earliest time in ms to fetch $orders for
             * @param {int} [$limit] the maximum number of order structures to retrieve
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array[]} a list of ~@link https://docs.ccxt.com/#/?id=order-structure order structures~
             */
            Async\await($this->load_markets());
            $topic = 'executionreport';
            $messageHash = $topic;
            if ($symbol !== null) {
                $market = $this->market($symbol);
                $symbol = $market['symbol'];
                $messageHash .= ':' . $symbol;
            }
            $request = array(
                'event' => 'subscribe',
                'topic' => $topic,
            );
            $message = array_merge($request, $params);
            $orders = Async\await($this->watch_private($messageHash, $message));
            if ($this->newUpdates) {
                $limit = $orders->getLimit ($symbol, $limit);
            }
            return $this->filter_by_symbol_since_limit($orders, $symbol, $since, $limit, true);
        }) ();
    }

