                $symbol = $market['symbol'];
                $messageHash .= ':' . $symbol;
            }
            $request = array(
                'event' => 'subscribe',
                'topic' => $topic,
            );
            $message = array_merge($request, $params);
            $trades = Async\await($this->watch_private($messageHash, $message));
            if ($this->newUpdates) {
                $limit = $trades->getLimit ($symbol, $limit);
            }
            return $this->filter_by_symbol_since_limit($trades, $symbol, $since, $limit, true);
        }) ();
    }

    public function parse_ws_order($order, $market = null) {
        //
        //     {
        //         "symbol" => "PERP_BTC_USDT",
        //         "clientOrderId" => 0,
        //         "orderId" => 52952826,
        //         "type" => "LIMIT",
        //         "side" => "SELL",
        //         "quantity" => 0.01,
        //         "price" => 22000,
        //         "tradeId" => 0,
        //         "executedPrice" => 0,
        //         "executedQuantity" => 0,
        //         "fee" => 0,
        //         "feeAsset" => "USDT",
        //         "totalExecutedQuantity" => 0,
        //         "status" => "NEW",
        //         "reason" => '',
        //         "orderTag" => "default",
        //         "totalFee" => 0,
        //         "visible" => 0.01,
        //         "timestamp" => 1657515556799,
        //         "reduceOnly" => false,
        //         "maker" => false
        //     }
        //
        $orderId = $this->safe_string($order, 'orderId');
        $marketId = $this->safe_string($order, 'symbol');
        $market = $this->market($marketId);
        $symbol = $market['symbol'];
        $timestamp = $this->safe_integer($order, 'timestamp');
        $fee = array(
            'cost' => $this->safe_string($order, 'totalFee'),
            'currency' => $this->safe_string($order, 'feeAsset'),
        );
        $price = $this->safe_number($order, 'price');
        $avgPrice = $this->safe_number($order, 'avgPrice');
        if (($price === 0) && ($avgPrice !== null)) {
            $price = $avgPrice;
        }
        $amount = $this->safe_float($order, 'quantity');
        $side = $this->safe_string_lower($order, 'side');
        $type = $this->safe_string_lower($order, 'type');
        $filled = $this->safe_number($order, 'totalExecutedQuantity');
        $totalExecQuantity = $this->safe_float($order, 'totalExecutedQuantity');
        $remaining = $amount;
        if ($amount >= $totalExecQuantity) {
            $remaining -= $totalExecQuantity;
        }
        $rawStatus = $this->safe_string($order, 'status');
        $status = $this->parse_order_status($rawStatus);
        $trades = null;
        $clientOrderId = $this->safe_string($order, 'clientOrderId');
        return $this->safe_order(array(
            'info' => $order,
            'symbol' => $symbol,
            'id' => $orderId,
            'clientOrderId' => $clientOrderId,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'lastTradeTimestamp' => $timestamp,
            'type' => $type,
            'timeInForce' => null,
            'postOnly' => null,
            'side' => $side,
            'price' => $price,
            'stopPrice' => null,
            'triggerPrice' => null,
            'amount' => $amount,
            'cost' => null,
            'average' => null,
            'filled' => $filled,
            'remaining' => $remaining,
            'status' => $status,
            'fee' => $fee,
            'trades' => $trades,
        ));
    }

    public function handle_order_update(Client $client, $message) {
        //
        //     {
        //         "topic" => "executionreport",
        //         "ts" => 1657515556799,
        //         "data" => {
        //             "symbol" => "PERP_BTC_USDT",
        //             "clientOrderId" => 0,
        //             "orderId" => 52952826,
        //             "type" => "LIMIT",
        //             "side" => "SELL",
        //             "quantity" => 0.01,
        //             "price" => 22000,
        //             "tradeId" => 0,
        //             "executedPrice" => 0,
        //             "executedQuantity" => 0,
        //             "fee" => 0,
        //             "feeAsset" => "USDT",
        //             "totalExecutedQuantity" => 0,
        //             "status" => "NEW",
        //             "reason" => '',
        //             "orderTag" => "default",
        //             "totalFee" => 0,
        //             "visible" => 0.01,
        //             "timestamp" => 1657515556799,
        //             "reduceOnly" => false,
        //             "maker" => false
        //         }
        //     }
        //
        $order = $this->safe_dict($message, 'data');
        $tradeId = $this->safe_string($order, 'tradeId');
