function test_eth_methods() {
    $exchange = new \ccxt\async\Exchange(array(
        'id' => 'sampleexchange',
    ));
    $private_key = '0x27c9c557bd398e354b57ba58046b055035c47788926eb53fcdb394769ef80e1b';
    $public_key = '0x3096cD9827766E03f8b6DF58996399406DC270Af';
    $generated_address = $exchange->eth_get_address_from_private_key($private_key);
    assert(strtolower($generated_address) === strtolower($public_key), 'ethGetAddressFromPrivateKey did not generate the expected address: ' . $generated_address . ' != ' . $public_key);
}
