function test_json() {
    $exchange = new \ccxt\async\Exchange(array(
        'id' => 'regirock',
    ));
    // Test: object
    $obj = array(
        'k' => 'v',
    );
    $obj_json = $exchange->json($obj);
    assert($obj_json === '{"k":"v"}');
    // Test: list
    $list = [1, 2];
    $list_json = $exchange->json($list);
    assert($list_json === '[1,2]');
}
