    public function parse_ticker($ticker, $market = null) {
        //  FetchTicker (v1)
        //
        //      {
        //          "bid":"0.021979",
        //          "ask":"0.021996",
        //          "open":"0.02182",
        //          "high":"0.022039",
        //          "low":"0.02161",
        //          "last":"0.021987",
        //          "volume":"2810.267",
        //          "deal":"61.383565474",
        //          "change":"0.76",
        //      }
        //
        // FetchTickers (v4)
        //
        //      "BCH_RUB":array(
        //          "base_id":1831,
        //          "quote_id":0,
        //          "last_price":"32830.21",
        //          "quote_volume":"1494659.8024096",
        //          "base_volume":"46.1083",
        //          "isFrozen":false,
        //          "change":"2.12" // in percent
        //      ),
        //
        $symbol = null;
        if ($market !== null) {
            $symbol = $market['symbol'];
        }
        $last = $this->safe_string($ticker, 'last_price');
        $change = $this->safe_string($ticker, 'change');
        $percentage = Precise::string_mul($change, '0.01');
        return $this->safe_ticker(array(
            'symbol' => $symbol,
            'timestamp' => null,
            'datetime' => null,
            'high' => $this->safe_string($ticker, 'high'),
            'low' => $this->safe_string($ticker, 'low'),
            'bid' => $this->safe_string($ticker, 'bid'),
            'bidVolume' => null,
            'ask' => $this->safe_string($ticker, 'ask'),
            'askVolume' => null,
            'vwap' => null,
            'open' => $this->safe_string($ticker, 'open'),
            'close' => $last,
            'last' => $last,
            'previousClose' => null,
            'change' => null,
            'percentage' => $percentage,
            'average' => null,
            'baseVolume' => $this->safe_string_2($ticker, 'base_volume', 'volume'),
            'quoteVolume' => $this->safe_string_2($ticker, 'quote_volume', 'deal'),
            'info' => $ticker,
        ), $market, false);
    }

    public function fetch_tickers($symbols = null, $params = array ()) {
        $this->load_markets();
        $response = $this->v4PublicGetTicker ($params);
        //
        //      "BCH_RUB" => array(
        //          "base_id":1831,
        //          "quote_id":0,
        //          "last_price":"32830.21",
        //          "quote_volume":"1494659.8024096",
        //          "base_volume":"46.1083",
        //          "isFrozen":false,
        //          "change":"2.12"
        //      ),
        //
        $marketIds = is_array($response) ? array_keys($response) : array();
        $result = array();
        for ($i = 0; $i < count($marketIds); $i++) {
            $marketId = $marketIds[$i];
            $market = $this->safe_market($marketId);
            $ticker = $this->parse_ticker($response[$marketId], $market);
            $symbol = $ticker['symbol'];
            $result[$symbol] = $ticker;
        }
        return $this->filter_by_array($result, 'symbol', $symbols);
    }

