    def fetch_currencies(self, params={}) -> Currencies:
        """
        fetches all available currencies on an exchange

        https://github.com/asterdex/api-docs/blob/master/aster-finance-api.md#exchange-information

        :param dict [params]: extra parameters specific to the exchange API endpoint
        :returns dict: an associative dictionary of currencies
        """
        response: dict = self.publicGetFapiV1ExchangeInfo(params)
        rows = self.safe_list(response, 'assets', [])
        #
        #     [
        #         {
        #             "asset": "USDT",
        #             "marginAvailable": True,
        #             "autoAssetExchange": "-10000"
        #         }
        #     ]
        #
        result: dict = {}
        for i in range(0, len(rows)):
            currency = rows[i]
            currencyId = self.safe_string(currency, 'asset')
            code = self.safe_currency_code(currencyId)
            result[code] = self.safe_currency_structure({
                'info': currency,
                'code': code,
                'id': currencyId,
                'name': code,
                'active': None,
                'deposit': None,
                'withdraw': None,
                'fee': None,
                'precision': None,
                'limits': {
                    'amount': {
                        'min': None,
                        'max': None,
                    },
                    'withdraw': {
                        'min': None,
                        'max': None,
                    },
                    'deposit': {
                        'min': None,
                        'max': None,
                    },
                },
                'networks': None,
                'type': 'crypto',  # atm exchange api provides only cryptos
            })
        return result

