    async def fetch_markets(self, symbols: Strings = None, params={}) -> List[Market]:
        """
        retrieves data on all markets for abantether
        :see: https://abantether.com/management/all-coins/?format=json
        :param dict [params]: extra parameters specific to the exchange API endpoint
        :returns dict[]: an array of objects representing market data
        """
        response = await self.publicGetManagementAllCoins(params)
        result = []
        quotes = ['IRT', 'USDT']
        for i in range(0, len(response)):
            base = self.safe_string(response[i], 'symbol')
            for index in range(0, len(quotes)):
                quote = quotes[index]
                response[i]['base'] = base
                response[i]['quote'] = quote
                if base == quote:
                    continue
                market = await self.parse_market(response[i])
                result.append(market)
        return result

