    async def fetch_markets(self, symbols: Strings = None, params={}) -> List[Market]:
        """
        retrieves data on all markets for arzplus
        :see: https://api.arzplus.net/
        :param dict [params]: extra parameters specific to the exchange API endpoint
        :returns dict[]: an array of objects representing market data
        """
        request = {
            'stats': '1',
            'enable': 'true',
        }
        response = await self.publicGetApiV1MarketSymbols(request)
        result = []
        for i in range(0, len(response)):
            market = await self.parse_market(response[i])
            result.append(market)
        return result

