    def parse_trade(self, trade: dict, market: Market = None) -> Trade:
        #
        # fetchTrades
        #     {
        #         "id": 8721564,
        #         "isBuyerMaker": False,
        #         "price": "117427.6",
        #         "quantity": "0.00016",
        #         "quoteQuantity": "18.788416",
        #         "timestamp": 1753123916818
        #     }
        #
        # fetchMyTrades
        #     {
        #         "clientId": null,
        #         "fee": "0.004974",
        #         "feeSymbol": "USDC",
        #         "isMaker": False,
        #         "orderId": "4238907375",
        #         "price": "3826.15",
        #         "quantity": "0.0026",
        #         "side": "Bid",
        #         "symbol": "ETH_USDC_PERP",
        #         "systemOrderType": null,
        #         "timestamp": "2025-07-27T17:39:00.092",
        #         "tradeId": 9748827
        #     }
        #
        id = self.safe_string_2(trade, 'id', 'tradeId')
        marketId = self.safe_string(trade, 'symbol')
        market = self.safe_market(marketId, market)
        price = self.safe_string(trade, 'price')
        amount = self.safe_string(trade, 'quantity')
        isMaker = self.safe_bool(trade, 'isMaker')
        takerOrMaker = 'maker' if isMaker else 'taker'
        orderId = self.safe_string(trade, 'orderId')
        side = self.parse_order_side(self.safe_string(trade, 'side'))
        fee = None
        feeAmount = self.safe_string(trade, 'fee')
        timestamp = self.safe_integer(trade, 'timestamp')
        if feeAmount is not None:
            # if fetchMyTrades
            datetime = self.safe_string(trade, 'timestamp')
            timestamp = self.parse8601(datetime)
        feeSymbol = self.safe_currency_code(self.safe_string(trade, 'feeSymbol'))
        if feeAmount is not None:
            fee = {
                'cost': feeAmount,
                'currency': feeSymbol,
                'rate': None,
            }
        return self.safe_trade({
            'info': trade,
            'timestamp': timestamp,
            'datetime': self.iso8601(timestamp),
            'symbol': market['symbol'],
            'id': id,
            'order': orderId,
            'type': None,
            'side': side,
            'takerOrMaker': takerOrMaker,
            'price': price,
            'amount': amount,
            'cost': None,
            'fee': fee,
        }, market)

