    def parse_order(self, order, market=None):
        #
        # spot
        # createOrder, cancelOrder
        #
        #    {
        #        "symbol": "XRP-USDT",
        #        "orderId": 1514090846268424192,
        #        "transactTime": 1649822362855,
        #        "price": "0.5",
        #        "origQty": "10",
        #        "executedQty": "0",
        #        "cummulativeQuoteQty": "0",
        #        "status": "PENDING",
        #        "type": "LIMIT",
        #        "side": "BUY"
        #    }
        #
        # fetchOrder
        #
        #    {
        #        symbol: 'ETH-USDT',
        #        orderId: '1660602123001266176',
        #        price: '1700',
        #        origQty: '0.003',
        #        executedQty: '0',
        #        cummulativeQuoteQty: '0',
        #        status: 'PENDING',
        #        type: 'LIMIT',
        #        side: 'BUY',
        #        time: '1684753373276',
        #        updateTime: '1684753373276',
        #        origQuoteOrderQty: '0',
        #        fee: '0',
        #        feeAsset: 'ETH'
        #    }
        #
        # fetchOpenOrders, fetchClosedOrders
        #
        #   {
        #       "symbol": "XRP-USDT",
        #       "orderId": 1514073325788200960,
        #       "price": "0.5",
        #       "origQty": "20",
        #       "executedQty": "0",
        #       "cummulativeQuoteQty": "0",
        #       "status": "PENDING",
        #       "type": "LIMIT",
        #       "side": "BUY",
        #       "time": 1649818185647,
        #       "updateTime": 1649818185647,
        #       "origQuoteOrderQty": "0"
        #   }
        #
        #
        # swap
        # createOrder
        #
        #    {
        #      "symbol": "BTC-USDT",
        #      "orderId": 1590973236294713344,
        #      "side": "BUY",
        #      "positionSide": "LONG",
        #      "type": "LIMIT"
        #    }
        #
        # fetchOrder, fetchOpenOrders, fetchClosedOrders
        #
        #    {
        #        "symbol": "LINK-USDT",
        #        "orderId": 1585839271162413056,
        #        "side": "BUY",
        #        "positionSide": "LONG",
        #        "type": "TRIGGER_MARKET",
        #        "origQty": "5.0",
        #        "price": "9",
        #        "executedQty": "0.0",
        #        "avgPrice": "0",
        #        "cumQuote": "0",
        #        "stopPrice": "5",
        #        "profit": "0.0000",
        #        "commission": "0.000000",
        #        "status": "CANCELLED",
        #        "time": 1667631605000,
        #        "updateTime": 1667631605000
        #    }
        #
        positionSide = self.safe_string(order, 'positionSide')
        marketType = 'spot' if (positionSide is None) else 'swap'
        marketId = self.safe_string(order, 'symbol')
        symbol = self.safe_symbol(marketId, market, '-', marketType)
        orderId = self.safe_string(order, 'orderId')
        side = self.safe_string_lower(order, 'side')
        type = self.safe_string_lower(order, 'type')
        timestamp = self.safe_integer_2(order, 'time', 'transactTime')
        lastTradeTimestamp = self.safe_integer(order, 'updateTime')
        price = self.safe_string(order, 'price')
        average = self.safe_string(order, 'avgPrice')
        amount = self.safe_string(order, 'origQty')
        filled = self.safe_string(order, 'executedQty')
        statusId = self.safe_string(order, 'status')
        fee = {
            'currency': self.safe_string(order, 'feeAsset'),
            'rate': self.safe_string_2(order, 'fee', 'commission'),
        }
        return self.safe_order({
            'info': order,
            'id': orderId,
            'clientOrderId': None,
            'timestamp': timestamp,
            'datetime': self.iso8601(timestamp),
            'lastTradeTimestamp': lastTradeTimestamp,
            'symbol': symbol,
            'type': type,
            'timeInForce': None,
            'postOnly': None,
            'side': side,
            'price': price,
            'stopPrice': self.safe_number(order, 'triggerPrice'),
            'triggerPrice': self.safe_number(order, 'triggerPrice'),
            'average': average,
            'cost': None,
            'amount': amount,
            'filled': filled,
            'remaining': None,
            'status': self.parse_order_status(statusId),
            'fee': fee,
            'trades': None,
        }, market)

    def parse_order_status(self, status):
        statuses = {
            'NEW': 'open',
            'PENDING': 'open',
            'PARTIALLY_FILLED': 'open',
            'FILLED': 'closed',
            'CANCELLED': 'canceled',
            'FAILED': 'failed',
        }
        return self.safe_string(statuses, status, status)

