    async def fetch_time(self, params={}):
        """
        fetches the current integer timestamp in milliseconds from the bingx server
        :see: https://bingx-api.github.io/docs/#/swapV2/base-info.html#Get%20Server%20Time
        :param dict [params]: extra parameters specific to the exchange API endpoint
        :returns int: the current integer timestamp in milliseconds from the bingx server
        """
        response = await self.swapV2PublicGetServerTime(params)
        #
        #    {
        #        "code": 0,
        #        "msg": "",
        #        "data": {
        #            "serverTime": 1675319535362
        #        }
        #    }
        #
        data = self.safe_dict(response, 'data')
        return self.safe_integer(data, 'serverTime')

    async def fetch_currencies(self, params={}) -> Currencies:
        """
        fetches all available currencies on an exchange
        :see: https://bingx-api.github.io/docs/#/common/account-api.html#All%20Coins
        :param dict [params]: extra parameters specific to the exchange API endpoint
        :returns dict: an associative dictionary of currencies
        """
        if not self.check_required_credentials(False):
            return None
        isSandbox = self.safe_bool(self.options, 'sandboxMode', False)
        if isSandbox:
            return None
        response = await self.walletsV1PrivateGetCapitalConfigGetall(params)
        #
        #    {
        #      "code": 0,
        #      "timestamp": 1702623271477,
        #      "data": [
        #        {
        #          "coin": "BTC",
        #          "name": "BTC",
        #          "networkList": [
        #            {
        #              "name": "BTC",
        #              "network": "BTC",
        #              "isDefault": True,
        #              "minConfirm": 2,
        #              "withdrawEnable": True,
        #              "depositEnable": True,
        #              "withdrawFee": "0.0006",
        #              "withdrawMax": "1.17522",
        #              "withdrawMin": "0.0005",
        #              "depositMin": "0.0002"
        #            },
        #            {
        #              "name": "BTC",
        #              "network": "BEP20",
        #              "isDefault": False,
        #              "minConfirm": 15,
        #              "withdrawEnable": True,
        #              "depositEnable": True,
        #              "withdrawFee": "0.0000066",
        #              "withdrawMax": "1.17522",
        #              "withdrawMin": "0.0000066",
        #              "depositMin": "0.0002"
        #            }
        #          ]
        #        }
        #      ]
        #    }
        #
        data = self.safe_list(response, 'data', [])
        result: dict = {}
        for i in range(0, len(data)):
            entry = data[i]
            currencyId = self.safe_string(entry, 'coin')
            code = self.safe_currency_code(currencyId)
            name = self.safe_string(entry, 'name')
            networkList = self.safe_list(entry, 'networkList')
            networks: dict = {}
            fee = None
            depositEnabled = False
            withdrawEnabled = False
            defaultLimits: dict = {}
            for j in range(0, len(networkList)):
                rawNetwork = networkList[j]
                network = self.safe_string(rawNetwork, 'network')
                networkCode = self.network_id_to_code(network)
                isDefault = self.safe_bool(rawNetwork, 'isDefault')
                networkDepositEnabled = self.safe_bool(rawNetwork, 'depositEnable')
                if networkDepositEnabled:
                    depositEnabled = True
                networkWithdrawEnabled = self.safe_bool(rawNetwork, 'withdrawEnable')
                if networkWithdrawEnabled:
                    withdrawEnabled = True
                limits: dict = {
                    'withdraw': {
                        'min': self.safe_number(rawNetwork, 'withdrawMin'),
                        'max': self.safe_number(rawNetwork, 'withdrawMax'),
                    },
                }
                fee = self.safe_number(rawNetwork, 'withdrawFee')
                if isDefault:
                    defaultLimits = limits
                precision = self.safe_number(rawNetwork, 'withdrawPrecision')
                networkActive = networkDepositEnabled or networkWithdrawEnabled
                networks[networkCode] = {
                    'info': rawNetwork,
                    'id': network,
                    'network': networkCode,
                    'fee': fee,
                    'active': networkActive,
                    'deposit': networkDepositEnabled,
                    'withdraw': networkWithdrawEnabled,
                    'precision': precision,
                    'limits': limits,
                }
            active = depositEnabled or withdrawEnabled
            result[code] = {
                'info': entry,
                'code': code,
                'id': currencyId,
                'precision': None,
                'name': name,
                'active': active,
                'deposit': depositEnabled,
                'withdraw': withdrawEnabled,
                'networks': networks,
                'fee': fee,
                'limits': defaultLimits,
            }
        return result

