    def add_pagination_cursor_to_result(self, response, data):
        endId = self.safe_value(response, 'endId')
        if endId is not None:
            dataLength = len(data)
            if dataLength > 0:
                first = data[0]
                last = data[dataLength - 1]
                first['endId'] = endId
                last['endId'] = endId
                data[0] = first
                data[dataLength - 1] = last
        return data

