    async def fetch_status(self, params={}):
        options = self.safe_value(self.options, 'fetchBalance', {})
        defaultType = self.safe_string(self.options, 'defaultType')
        type = self.safe_string(options, 'type', defaultType)
        type = self.safe_string(params, 'type', type)
        params = self.omit(params, 'type')
        response = await self.publicSystemGetService(params)
        #
        #     {
        #         "code": 1000,
        #         "trace":"886fb6ae-456b-4654-b4e0-d681ac05cea1",
        #         "message": "OK",
        #         "data": {
        #             "serivce":[
        #                 {
        #                     "title": "Spot API Stop",
        #                     "service_type": "spot",
        #                     "status": "2",
        #                     "start_time": 1527777538000,
        #                     "end_time": 1527777538000
        #                 },
        #                 {
        #                     "title": "Contract API Stop",
        #                     "service_type": "contract",
        #                     "status": "2",
        #                     "start_time": 1527777538000,
        #                     "end_time": 1527777538000
        #                 }
        #             ]
        #         }
        #     }
        #
        data = self.safe_value(response, 'data', {})
        services = self.safe_value(data, 'service', [])
        servicesByType = self.index_by(services, 'service_type')
        if (type == 'swap') or (type == 'future'):
            type = 'contract'
        service = self.safe_value(servicesByType, type)
        status = None
        eta = None
        if service is not None:
            statusCode = self.safe_integer(service, 'status')
            if statusCode == 2:
                status = 'ok'
            else:
                status = 'maintenance'
                eta = self.safe_integer(service, 'end_time')
        self.status = self.extend(self.status, {
            'status': status,
            'updated': self.milliseconds(),
            'eta': eta,
        })
        return self.status

    async def fetch_spot_markets(self, params={}):
        response = await self.publicSpotGetSymbolsDetails(params)
        #
        #     {
        #         "message":"OK",
        #         "code":1000,
        #         "trace":"a67c9146-086d-4d3f-9897-5636a9bb26e1",
        #         "data":{
