        #             "startTime" : 1659606240000,
        #             "closeTime" : 1659692648742,
        #             "displayName" : "KUB/USDD",
        #             "dailyChange" : "0.00",
        #             "ts" : 1659692648742
        #         }
        #     ]
        #
        return self.parse_tickers(response, symbols)

