    def parse_ticker(self, ticker, market=None):
        #
        #     {
        #         "symbol" : "BTC_USDT",
        #         "open" : "22814.93",
        #         "low" : "22441.90",
        #         "high" : "23413.00",
        #         "close" : "23148.66",
        #         "quantity" : "71.743706",
        #         "amount" : "1638994.52683452",
        #         "tradeCount" : 3893,
        #         "startTime" : 1659605760000,
        #         "closeTime" : 1659692161077,
        #         "displayName" : "BTC/USDT",
        #         "dailyChange" : "0.0152",
        #         "ts" : 1659692169838
        #     }
        #
        timestamp = self.safe_integer(ticker, 'ts')
        marketId = self.safe_string(ticker, 'symbol')
        market = self.safe_market(marketId)
        close = self.safe_string(ticker, 'close')
        relativeChange = self.safe_string(ticker, 'percentChange')
        percentage = Precise.string_mul(relativeChange, '100')
        return self.safe_ticker({
            'id': marketId,
            'symbol': market['symbol'],
            'timestamp': timestamp,
            'datetime': self.iso8601(timestamp),
            'high': self.safe_string(ticker, 'high'),
            'low': self.safe_string(ticker, 'low'),
            'bid': None,
            'bidVolume': None,
            'ask': None,
            'askVolume': None,
            'vwap': None,
            'open': self.safe_string(ticker, 'open'),
            'close': close,
            'last': close,
            'previousClose': None,
            'change': None,
            'percentage': percentage,
            'average': None,
            'baseVolume': self.safe_string(ticker, 'quantity'),
            'quoteVolume': self.safe_string(ticker, 'amount'),
            'info': ticker,
        }, market)

