    def get_symbol_id_to_risk_limits(self):
        risk_limits = self.get_raw_risk_limits()
        symbol_id_to_risk_limits = defaultdict(list)
        for risk_limit in risk_limits:
            symbol_id = self.safe_string(risk_limit, "symbol")
            symbol_id_to_risk_limits[symbol_id].append(risk_limit)
        return symbol_id_to_risk_limits

    def parse_symbol_risk_limits(self, risk_limits):
        unified_risk_limits = list()
        for risk_limit in risk_limits:
            limit = self.safe_float(risk_limit, 'riskLimitValue')
            max_leverage = self.safe_float(risk_limit, 'maxLeverage')
            risk_id = self.safe_integer(risk_limit, "id")
            unified_risk_limits.append({"id": risk_id, "limit": limit, "max_leverage": max_leverage})
        return sorted(unified_risk_limits, key=lambda risk_limit_dict: risk_limit_dict["limit"])

    def get_unified_risk_limits_for_symbol(self, symbol_id_to_risk_limits, _id):
        risk_limits = self.safe_value(symbol_id_to_risk_limits, _id, list())
        return self.parse_symbol_risk_limits(risk_limits)

    def fetch_future_markets(self, params):
        params = self.extend(params)
        limit = 1000
        params['limit'] = limit  # minimize number of requests
        response = self.publicGetV5MarketInstrumentsInfo(params)
        data = self.safe_value(response, 'result', {})
        markets = self.safe_value(data, 'list', [])
        paginationCursor = self.safe_string(data, 'nextPageCursor')
        if paginationCursor:
            while paginationCursor:
                params['cursor'] = paginationCursor
                responseInner = self.publicGetV5MarketInstrumentsInfo(params)
                dataNew = self.safe_value(responseInner, 'result', {})
                rawMarkets = self.safe_value(dataNew, 'list', [])
                if len(rawMarkets) < limit:
                    break
                markets = self.array_concat(rawMarkets, markets)
