    @staticmethod
    def _get_best_risk_id(risk_limits, desired_limit):
        for risk_limit in risk_limits:
            if desired_limit <= risk_limit['limit']:
                return risk_limit['id']

    def set_risk_limit(self, symbol, is_long=None, limit=None, risk_id=None):
        self.load_markets()
        assert limit or risk_id
        if risk_id is None:
            raw_risk_limits = self.get_raw_risk_limits(symbol=symbol)
            risk_limits = self.parse_symbol_risk_limits(raw_risk_limits)
            risk_id = self._get_best_risk_id(risk_limits, limit)

        request = {"symbol": self.market_id(symbol), "riskId": risk_id}
        try:
            if self.is_linear():
                request['category'] = 'linear'
                if is_long is not None:
                    request['positionIdx'] = 1 if is_long else 2
            elif self.is_inverse():
                request['category'] = 'inverse'
            else:
                raise NotSupported()
            response = self.privatePostV5PositionSetRiskLimit(request)
        except NotChanged:
            return risk_id

        result = self.safe_value(response, 'result')
        return self.safe_integer(result, 'riskId')

