    def fetch_currencies(self, params={}):
        response = self.publicGetCommonAssetConfig(params)
        #     {
        #         "code": 0,
        #         "data": {
        #             "USDT-ERC20": {
        #                  "asset": "USDT",
        #                  "chain": "ERC20",
        #                  "withdrawal_precision": 6,
        #                  "can_deposit": True,
