        #     maintMarginReqEr: '1000000',
        #     maintMarginReq: '0.01000000',
        #     riskLimitEv: '5000000000',
        #     riskLimit: '500000.00000000',
        #     size: '1',
        #     value: '22.22370000',
        #     valueEv: '222237',
        #     avgEntryPriceEp: '44447400',
