        if limit > 200:
            raise BadRequest(self.id + ' fetchFundingHistory() limit argument cannot exceed 200')
        if limit is not None:
            request['limit'] = limit
        response = self.privateGetApiDataFuturesFundingFees(self.extend(request, params))
        #
