import ccxt.async_support as ccxt  # noqa: F402
from ccxt.base.errors import BadRequest  # noqa E402

def test_json():
    exchange = ccxt.Exchange({
        'id': 'regirock',
    })
    # Test: object
    obj = {
        'k': 'v',
    }
    obj_json = exchange.json(obj)
    assert obj_json == '{"k":"v"}'
    # Test: list
    list = [1, 2]
    list_json = exchange.json(list)
    assert list_json == '[1,2]'
