    def parse_trade(self, trade, market=None):
        #
        # Spot
        #
        #     {
        #         "date":1624537391,
        #         "amount":"0.0142",
        #         "price":"33936.42",
        #         "trade_type":"ask",
        #         "type":"sell",
        #         "tid":1718869018
        #     }
        #
        # Swap
        #
        #     {
        #         "amount": "0.002",
        #         "createTime": "1645787446034",
        #         "feeAmount": "-0.05762699",
        #         "feeCurrency": "USDT",
        #         "id": "6902932868050395136",
        #         "maker": False,
        #         "orderId": "6902932868042006528",
        #         "price": "38417.99",
        #         "relizedPnl": "0.30402",
        #         "side": 4,
        #         "userId": "6896693805014120448"
        #     },
        #
        sideField = 'side' if market['swap'] else 'trade_type'
        side = self.safe_string(trade, sideField)
        takerOrMaker = None
        maker = self.safe_value(trade, 'maker')
        if maker is not None:
            takerOrMaker = 'maker' if maker else 'taker'
        if market['spot']:
            side = 'buy' if (side == 'bid') else 'sell'
        else:
            if side == '3':
                side = 'sell'  # close long
            elif side == '4':
                side = 'buy'  # close short
            elif side == '1':
                side = 'buy'  # open long
            elif side == '2':
                side = 'sell'  # open short
        timestamp = None
        if market['swap']:
            timestamp = self.safe_integer(trade, 'createTime')
        else:
            timestamp = self.safe_timestamp(trade, 'date')
        price = self.safe_string(trade, 'price')
        amount = self.safe_string(trade, 'amount')
        fee = None
        feeCostString = self.safe_string(trade, 'feeAmount')
        if feeCostString is not None:
            feeCurrencyId = self.safe_string(trade, 'feeCurrency')
            fee = {
                'cost': feeCostString,
                'currency': self.safe_currency_code(feeCurrencyId),
            }
        market = self.safe_market(None, market)
        return self.safe_trade({
            'info': trade,
            'id': self.safe_string(trade, 'tid'),
            'timestamp': timestamp,
            'datetime': self.iso8601(timestamp),
            'symbol': market['symbol'],
            'type': None,
            'side': side,
            'order': self.safe_string(trade, 'orderId'),
            'takerOrMaker': takerOrMaker,
            'price': price,
            'amount': amount,
            'cost': None,
            'fee': fee,
        }, market)

