        #             ],
        #             "pageNum": 1,
        #             "pageSize": 10
        #         },
        #         "desc": "操作成功"
        #     }
        #
        if stop:
            data = self.safe_value(response, 'data', {})
            response = self.safe_value(data, 'list', [])
        result = []
