        #         status: 2,
        #         total_amount: 0.0002,
        #         trade_amount: 0.0002,
        #         trade_date: 1614515104998,
        #         trade_money: 8.983712,
        #         type: 1,
        #         useZbFee: False
        #     },
        #
        # Swap fetchOrder
        #
        #     {
        #         "action": 1,
        #         "amount": "0.002",
        #         "availableAmount": "0.002",
        #         "availableValue": "60",
        #         "avgPrice": "0",
        #         "canCancel": True,
        #         "cancelStatus": 20,
        #         "createTime": "1646185684379",
