    def cost_to_precision(symbol, cost)
      return self.decimal_to_precision(cost, TRUNCATE, self.markets[symbol]['precision']['price'], DECIMAL_PLACES)
    end

    def fee_to_precision(symbol, fee)
      return self.decimal_to_precision(fee, TRUNCATE, self.markets[symbol]['precision']['price'], DECIMAL_PLACES)
    end

    def fetch_markets(params = {})
      # https://github.com/ccxt/ccxt/commit/866370ba6c9cabaf5995d992c15a82e38b8ca291
      # https://github.com/ccxt/ccxt/pull/4304
      response = self.publicGetMarkets
      result = []
      markets = self.safe_value(response, 'result')
      for i in (0...markets.length)
        market = markets[i]
        id = market['MarketName']
        baseId = market['MarketCurrency']
        quoteId = market['BaseCurrency']
        base = self.common_currency_code(baseId)
        quote = self.common_currency_code(quoteId)
        symbol = base + '/' + quote
        pricePrecision = 8
        if self.options['pricePrecisionByCode'].include?(quote)
          pricePrecision = self.options['pricePrecisionByCode'][quote]
        end
        precision = {
          'amount' => 8,
          'price' => pricePrecision
        }
        # bittrex uses boolean values, bleutrade uses strings
        active = self.safe_value(market, 'IsActive', false)
        if (active != 'false') || active
          active = true
        end
        result.push({
          'id' => id,
          'symbol' => symbol,
          'base' => base,
          'quote' => quote,
          'baseId' => baseId,
          'quoteId' => quoteId,
          'active' => active,
          'info' => market,
          'precision' => precision,
          'limits' => {
            'amount' => {
              'min' => market['MinTradeSize'],
              'max' => nil
            },
            'price' => {
              'min' => 10**-precision['price'],
              'max' => nil
            }
          }
        })
      end
      return result
    end

    def fetch_balance(params = {})
      self.load_markets
      response = self.accountGetBalances(params)
      balances = response['result']
      result = { 'info' => balances }
      indexed = self.index_by(balances, 'Currency')
      keys = indexed.keys
      for i in (0...keys.length)
        id = keys[i]
        currency = self.common_currency_code(id)
        account = self.account
        balance = indexed[id]
        free = self.safe_float(balance, 'Available', 0)
        total = self.safe_float(balance, 'Balance', 0)
        used = nil
        if total != nil
          if free != nil
            used = total - free
          end
        end
        account['free'] = free
        account['used'] = used
        account['total'] = total
        result[currency] = account
      end
      return self.parse_balance(result)
    end

    def fetch_order_book(symbol, limit = nil, params = {})
      self.load_markets
      response = self.publicGetOrderbook(self.shallow_extend({
        'market' => self.market_id(symbol),
        'type' => 'both'
      }, params))
      orderbook = response['result']
      if params.include?('type')
        if params['type'] == 'buy'
          orderbook = {
            'buy' => response['result'],
            'sell' => []
          }
        elsif params['type'] == 'sell'
          orderbook = {
            'buy' => [],
            'sell' => response['result']
          }
        end
      end
      return self.parse_order_book(orderbook, nil, 'buy', 'sell', 'Rate', 'Quantity')
    end

    def parse_ticker(ticker, market = nil)
      timestamp = self.safe_string(ticker, 'TimeStamp')
      if timestamp.is_a?(String)
        if timestamp.length > 0
          timestamp = self.parse8601(timestamp)
        end
      end
      symbol = nil
      if market
        symbol = market['symbol']
      end
      previous = self.safe_float(ticker, 'PrevDay')
      last = self.safe_float(ticker, 'Last')
      change = nil
      percentage = nil
      if last != nil
        if previous != nil
          change = last - previous
          if previous > 0
            percentage = (change / previous) * 100
          end
        end
      end
      return {
        'symbol' => symbol,
        'timestamp' => timestamp,
        'datetime' => self.iso8601(timestamp),
        'high' => self.safe_float(ticker, 'High'),
        'low' => self.safe_float(ticker, 'Low'),
        'bid' => self.safe_float(ticker, 'Bid'),
        'bidVolume' => nil,
        'ask' => self.safe_float(ticker, 'Ask'),
        'askVolume' => nil,
        'vwap' => nil,
        'open' => previous,
        'close' => last,
        'last' => last,
        'previousClose' => nil,
        'change' => change,
        'percentage' => percentage,
        'average' => nil,
        'baseVolume' => self.safe_float(ticker, 'Volume'),
        'quoteVolume' => self.safe_float(ticker, 'BaseVolume'),
        'info' => ticker
      }
    end

    def fetch_currencies(params = {})
      response = self.publicGetCurrencies(params)
      #
      #     {
      #         "success" => true,
      #         "message" => "",
      #         "result" => [
      #             {
      #                 "Currency" => "BTC",
      #                 "CurrencyLong":"Bitcoin",
      #                 "MinConfirmation":2,
      #                 "TxFee":0.00050000,
      #                 "IsActive":true,
      #                 "IsRestricted":false,
      #                 "CoinType":"BITCOIN",
      #                 "BaseAddress":"1N52wHoVR79PMDishab2XmRHsbekCdGquK",
      #                 "Notice":null
      #             },
      #             ...,
      #         ]
      #     }
      #
      currencies = self.safe_value(response, 'result', [])
      result = {}
      for i in (0...currencies.length)
        currency = currencies[i]
        id = self.safe_string(currency, 'Currency')
        # todo => will need to rethink the fees
        # to add support for multiple withdrawal/deposit methods and
        # differentiated fees for each particular method
        code = self.common_currency_code(id)
        precision = 8 # default precision, todo => fix "magic constants"
        address = self.safe_value(currency, 'BaseAddress')
        fee = self.safe_float(currency, 'TxFee') # todo => redesign
        result[code] = {
          'id' => id,
          'code' => code,
          'address' => address,
          'info' => currency,
          'type' => currency['CoinType'],
          'name' => currency['CurrencyLong'],
          'active' => currency['IsActive'],
          'fee' => fee,
          'precision' => precision,
          'limits' => {
            'amount' => {
              'min' => 10**-precision,
              'max' => 10**precision
            },
            'price' => {
              'min' => 10**-precision,
              'max' => 10**precision
            },
            'cost' => {
              'min' => nil,
              'max' => nil
            },
            'withdraw' => {
              'min' => fee,
              'max' => 10**precision
            }
          }
        }
      end
      return result
    end

    def fetch_tickers(symbols = nil, params = {})
      self.load_markets
      response = self.publicGetMarketsummaries(params)
      tickers = response['result']
      result = {}
      for t in (0...tickers.length)
        ticker = tickers[t]
        id = ticker['MarketName']
        market = nil
        symbol = id
        if self.markets_by_id.include?(id)
          market = self.markets_by_id[id]
          symbol = market['symbol']
        else
          symbol = self.parse_symbol(id)
        end
        result[symbol] = self.parse_ticker(ticker, market)
      end
      return result
    end

    def fetch_ticker(symbol, params = {})
      self.load_markets
      market = self.market(symbol)
      response = self.publicGetMarketsummary(self.shallow_extend({
        'market' => market['id']
      }, params))
      ticker = response['result'][0]
      return self.parse_ticker(ticker, market)
    end

    def parse_trade(trade, market = nil)
      timestamp = self.parse8601(trade['TimeStamp'] + '+00:00')
      side = nil
      if trade['OrderType'] == 'BUY'
        side = 'buy'
      elsif trade['OrderType'] == 'SELL'
        side = 'sell'
      end
      id = self.safe_string_2(trade, 'Id', 'ID')
      symbol = nil
      if market != nil
        symbol = market['symbol']
      end
      cost = nil
      price = self.safe_float(trade, 'Price')
      amount = self.safe_float(trade, 'Quantity')
      if amount != nil
        if price != nil
          cost = price * amount
        end
      end
      return {
        'info' => trade,
        'timestamp' => timestamp,
        'datetime' => self.iso8601(timestamp),
        'symbol' => symbol,
        'id' => id,
        'order' => nil,
        'type' => 'limit',
        'takerOrMaker' => nil,
        'side' => side,
        'price' => price,
        'amount' => amount,
        'cost' => cost,
        'fee' => nil
      }
    end

    def fetch_trades(symbol, since = nil, limit = nil, params = {})
      self.load_markets
      market = self.market(symbol)
      response = self.publicGetMarkethistory(self.shallow_extend({
        'market' => market['id']
      }, params))
      if response.include?('result')
        if response['result'] != nil
          return self.parse_trades(response['result'], market, since, limit)
        end
      end
      raise(ExchangeError, self.id + ' fetchTrades returned nil response')
    end

    def parse_ohlcv(ohlcv, market = nil, timeframe = '1d', since = nil, limit = nil)
      timestamp = self.parse8601(ohlcv['T'] + '+00:00')
      return [
        timestamp,
        ohlcv['O'],
        ohlcv['H'],
        ohlcv['L'],
        ohlcv['C'],
        ohlcv['V']
      ]
    end

    def fetch_ohlcv(symbol, timeframe = '1m', since = nil, limit = nil, params = {})
      self.load_markets
      market = self.market(symbol)
      request = {
        'tickInterval' => self.timeframes[timeframe],
        'marketName' => market['id']
      }
      response = self.v2GetMarketGetTicks(self.shallow_extend(request, params))
      if response.include?('result')
        if response['result']
          return self.parse_ohlcvs(response['result'], market, timeframe, since, limit)
        end
      end
    end

    def fetch_open_orders(symbol = nil, since = nil, limit = nil, params = {})
      self.load_markets
      request = {}
      market = nil
      if symbol != nil
        market = self.market(symbol)
        request['market'] = market['id']
      end
      response = self.marketGetOpenorders(self.shallow_extend(request, params))
      orders = self.parse_orders(response['result'], market, since, limit)
      return self.filter_by_symbol(orders, symbol)
    end

    def create_order(symbol, type, side, amount, price = nil, params = {})
      if type != 'limit'
        raise(ExchangeError, self.id + ' allows limit orders only')
      end
      self.load_markets
      market = self.market(symbol)
      method = 'marketGet' + self.capitalize(side) + type
      order = {
        'market' => market['id'],
        'quantity' => self.amount_to_precision(symbol, amount),
        'rate' => self.price_to_precision(symbol, price)
      }
      # if type == 'limit'
      #     order['rate'] = self.price_to_precision(symbol, price)
      response = self.send_wrapper(method, self.shallow_extend(order, params))
      orderIdField = self.get_order_id_field
      result = {
        'info' => response,
        'id' => response['result'][orderIdField],
        'symbol' => symbol,
        'type' => type,
        'side' => side,
        'status' => 'open'
      }
      return result
    end

    def get_order_id_field
      return 'uuid'
    end

    def cancel_order(id, symbol = nil, params = {})
      self.load_markets
      orderIdField = self.get_order_id_field
      request = {}
      request[orderIdField] = id
      response = self.marketGetCancel(self.shallow_extend(request, params))
      return self.shallow_extend(self.parse_order(response), {
        'status' => 'canceled'
      })
    end

    def fetch_deposits(code = nil, since = nil, limit = nil, params = {})
      self.load_markets
      # https://support.bittrex.com/hc/en-us/articles/115003723911
      request = {}
      currency = nil
      if code != nil
        currency = self.currency(code)
        request['currency'] = currency['id']
      end
      response = self.accountGetDeposithistory(self.shallow_extend(request, params))
      #
      #     { success =>    true,
      #       message =>   "",
      #        result => [{            Id =>  22578097,
      #                           Amount =>  0.3,
      #                         Currency => "ETH",
      #                    Confirmations =>  15,
      #                      LastUpdated => "2018-06-10T07:12:10.57",
      #                             TxId => "0xf50b5ba2ca5438b58f93516eaa523eaf35b4420ca0f24061003df1be7…",
      #                    CryptoAddress => "0xb25f281fa51f1635abd4a60b0870a62d2a7fa404"                    }] }
      #
      # we cannot filter by `since` timestamp, as it isn't set by Bittrex
      # see https://github.com/ccxt/ccxt/issues/4067
      # return self.parseTransactions(response['result'], currency, since, limit)
      return self.parseTransactions(response['result'], currency, nil, limit)
    end

