      if code.nil?
        raise(ExchangeError, self.id + ' => fetchDeposits requires a currency code argument')
      end
      currency = self.currency(code)
      response = self.privateGetCurrenciesCurrencyWithdrawals(self.shallow_extend({
        'currency' => currency['id'],
