    def fetch_markets(params = {})
      quoteIds = self.options['quoteIds']
      result = []
      for b in (0...quoteIds.length)
        quoteId = quoteIds[b]
        response = self.webGetSymbolTickerRightCoinQuote({
          'quote' => quoteId
        })
        tickers = self.safe_value(response, 'data', [])
        for i in (0...tickers.length)
          ticker = tickers[i]
          id = ticker['symbol']
          baseId = id.split('_')[0]
          base = baseId.upcase
          quote = quoteId.upcase
          base = self.common_currency_code(base)
          quote = self.common_currency_code(quote)
          symbol = base + '/' + quote
          precision = {
            'amount' => 8,
            'price' => 8
          }
          result.push({
            'id' => id,
            'symbol' => symbol,
            'base' => base,
            'quote' => quote,
            'baseId' => baseId,
            'quoteId' => quoteId,
            'active' => true,
            'precision' => precision,
            'limits' => {
              'amount' => {
                'min' => 10**-precision['amount'],
                'max' => 10**precision['amount']
              },
              'price' => {
                'min' => 10**-precision['price'],
                'max' => 10**precision['price']
              },
              'cost' => {
                'min' => nil,
                'max' => nil
              }
            },
            'info' => ticker
          })
        end
      end
      return result
    end

    def parse_ticker(ticker, market = nil)
      symbol = market['symbol']
      timestamp = self.milliseconds
      last = self.safe_float(ticker, 'last')
      percentage = self.safe_float(ticker, 'change')
      open = nil
      change = nil
      average = nil
      if percentage != nil
        relativeChange = percentage / 100
        open = last / self.sum(1, relativeChange)
        change = last - open
        average = self.sum(last, open) / 2
      end
      return {
        'symbol' => symbol,
        'timestamp' => timestamp,
        'datetime' => self.iso8601(timestamp),
        'high' => self.safe_float(ticker, 'high'),
        'low' => self.safe_float(ticker, 'low'),
        'bid' => self.safe_float(ticker, 'buy'),
        'bidVolume' => nil,
        'ask' => self.safe_float(ticker, 'sell'),
        'askVolume' => nil,
        'vwap' => nil,
        'open' => open,
        'close' => last,
        'last' => last,
        'previousClose' => nil,
        'change' => change,
        'percentage' => percentage,
        'average' => average,
        'baseVolume' => self.safe_float(ticker, 'vol'),
        'quoteVolume' => self.safe_float(ticker, 'quoteVol'),
        'info' => ticker
      }
    end

