    def parse_ohlcv(ohlcv, market = nil, timeframe = '1m', since = nil, limit = nil)
      return [
        ohlcv[0] * 1000,
        ohlcv[3],
        ohlcv[2],
        ohlcv[1],
        ohlcv[4],
        ohlcv[5]
      ]
    end

    def fetch_ohlcv(symbol, timeframe = '1m', since = nil, limit = nil, params = {})
      self.load_markets
      market = self.market(symbol)
      granularity = self.timeframes[timeframe]
      request = {
        'id' => market['id'],
        'granularity' => granularity
      }
      if since != nil
        request['start'] = self.ymdhms(since)
        if limit.nil?
          # https://docs.gdax.com/#get-historic-rates
          limit = 300 # max = 300
        end
        request['end'] = self.ymdhms(self.sum(limit * granularity * 1000, since))
      end
      response = self.publicGetProductsIdCandles(self.shallow_extend(request, params))
      return self.parse_ohlcvs(response, market, timeframe, since, limit)
    end

