    def parse_order_status(status)
      statuses = {
        'pending' => 'open',
        'active' => 'open',
        'open' => 'open',
        'done' => 'closed',
        'canceled' => 'canceled',
        'canceling' => 'open'
      }
      return self.safe_string(statuses, status, status)
    end

    def parse_order(order, market = nil)
      timestamp = self.parse8601(order['created_at'])
      symbol = nil
      if market.nil?
        if self.markets_by_id.include?(order['product_id'])
          market = self.markets_by_id[order['product_id']]
        end
      end
      status = self.parse_order_status(self.safe_string(order, 'status'))
      price = self.safe_float(order, 'price')
      amount = self.safe_float(order, 'size')
      if amount.nil?
        amount = self.safe_float(order, 'funds')
      end
      if amount.nil?
        amount = self.safe_float(order, 'specified_funds')
      end
      filled = self.safe_float(order, 'filled_size')
      remaining = nil
      if amount != nil
        if filled != nil
          remaining = amount - filled
        end
      end
      cost = self.safe_float(order, 'executed_value')
      fee = {
        'cost' => self.safe_float(order, 'fill_fees'),
        'currency' => nil,
        'rate' => nil
      }
      if market
        symbol = market['symbol']
      end
      return {
        'id' => order['id'],
        'info' => order,
        'timestamp' => timestamp,
        'datetime' => self.iso8601(timestamp),
        'lastTradeTimestamp' => nil,
        'status' => status,
        'symbol' => symbol,
        'type' => order['type'],
        'side' => order['side'],
        'price' => price,
        'cost' => cost,
        'amount' => amount,
        'filled' => filled,
        'remaining' => remaining,
        'fee' => fee
      }
    end

