    def parse_ticker(ticker, market = nil)
      #
      #     {
      #         'buy' => '0.00001168',
      #         'changePrice' => '-0.00000018',
      #         'changeRate' => '-0.0151',
      #         'datetime' => 1550661146316,
      #         'high' => '0.0000123',
      #         'last' => '0.00001169',
      #         'low' => '0.00001159',
      #         'sell' => '0.00001182',
      #         'symbol' => 'LOOM-BTC',
      #         'vol' => '44399.5669'
      #     }
      #
      percentage = self.safe_float(ticker, 'changeRate')
      if percentage != nil
        percentage = percentage * 100
      end
      last = self.safe_float(ticker, 'last')
      symbol = nil
      marketId = self.safe_string(ticker, 'symbol')
      if marketId != nil
        if self.markets_by_id.include?(marketId)
          market = self.markets_by_id[marketId]
          symbol = market['symbol']
        else
          baseId, quoteId = marketId.split('-')
          base = self.common_currency_code(baseId)
          quote = self.common_currency_code(quoteId)
          symbol = base + '/' + quote
        end
      end
      if symbol.nil?
        if market != nil
          symbol = market['symbol']
        end
      end
      return {
        'symbol' => symbol,
        'timestamp' => nil,
        'datetime' => nil,
        'high' => self.safe_float(ticker, 'high'),
        'low' => self.safe_float(ticker, 'low'),
        'bid' => self.safe_float(ticker, 'buy'),
        'bidVolume' => nil,
        'ask' => self.safe_float(ticker, 'sell'),
        'askVolume' => nil,
        'vwap' => nil,
        'open' => self.safe_float(ticker, 'open'),
        'close' => last,
        'last' => last,
        'previousClose' => nil,
        'change' => self.safe_float(ticker, 'changePrice'),
        'percentage' => percentage,
        'average' => nil,
        'baseVolume' => self.safe_float(ticker, 'vol'),
        'quoteVolume' => self.safe_float(ticker, 'volValue'),
        'info' => ticker
      }
    end

    def fetch_tickers(symbols = nil, params = {})
      self.load_markets
      response = self.publicGetMarketAllTickers(params)
      #
      #     {
      #         "code" => "200000",
      #         "data" => {
      #             "date" => 1550661940645,
      #             "ticker" => [
      #                 'buy' => '0.00001168',
