      #                 'high' => '0.0000123',
      #                 'last' => '0.00001169',
      #                 'low' => '0.00001159',
      #                 'sell' => '0.00001182',
      #                 'symbol' => 'LOOM-BTC',
      #                 'vol' => '44399.5669'
      #             },
      #         ]
      #     }
      #
      data = self.safe_value(response, 'data', {})
      tickers = self.safe_value(data, 'ticker', [])
      result = {}
      for i in (0...tickers.length)
        ticker = self.parse_ticker(tickers[i])
        symbol = self.safe_string(ticker, 'symbol')
        if symbol != nil
