    def parse_ohlcv(ohlcv, market = nil, timeframe = '1m', since = nil, limit = nil)
      #
      #     [
      #         "1545904980",             # Start time of the candle cycle
      #         "0.058",                  # opening price
      #         "0.049",                  # closing price
      #         "0.058",                  # highest price
      #         "0.049",                  # lowest price
      #         "0.018",                  # base volume
      #         "0.000945",               # quote volume
      #     ]
      #
      return [
        parse_int(ohlcv[0]) * 1000,
        parse_float(ohlcv[1]),
        parse_float(ohlcv[3]),
        parse_float(ohlcv[4]),
        parse_float(ohlcv[2]),
        parse_float(ohlcv[5])
      ]
    end

    def fetch_ohlcv(symbol, timeframe = '15m', since = nil, limit = nil, params = {})
      self.load_markets
      market = self.market(symbol)
      marketId = market['id']
      request = {
        'symbol' => marketId,
        'endAt' => self.seconds, # required param
        'type' => self.timeframes[timeframe]
      }
      if since != nil
        request['startAt'] = since / 1000.floor
      end
      response = self.publicGetMarketCandles(self.shallow_extend(request, params))
      responseData = response['data']
      return self.parse_ohlcvs(responseData, market, timeframe, since, limit)
    end

    def create_deposit_address(code, params = {})
      self.load_markets
      currencyId = self.currencyId(code)
      request = { 'currency' => currencyId }
      response = self.privatePostDepositAddresses(self.shallow_extend(request, params))
      # BCH {"code":"200000","data":{"address":"bitcoincash:qza3m4nj9rx7l9r0cdadfqxts6f92shvhvr5ls4q7z","memo":""}}
      # BTC {"code":"200000","data":{"address":"36SjucKqQpQSvsak9A7h6qzFjrVXpRNZhE","memo":""}}
      data = self.safe_value(response, 'data', {})
      address = self.safe_string(data, 'address')
      # BCH/BSV is returned with a "bitcoincash:" prefix, which we cut off here and only keep the address
      address = address.gsub('bitcoincash:', '')
      tag = self.safe_string(data, 'memo')
      self.check_address(address)
      return {
        'info' => response,
        'currency' => code,
        'address' => address,
        'tag' => tag
      }
    end

    def fetch_deposit_address(code, params = {})
      self.load_markets
      currencyId = self.currencyId(code)
      request = { 'currency' => currencyId }
      response = self.privateGetDepositAddresses(self.shallow_extend(request, params))
      # BCH {"code":"200000","data":{"address":"bitcoincash:qza3m4nj9rx7l9r0cdadfqxts6f92shvhvr5ls4q7z","memo":""}}
      # BTC {"code":"200000","data":{"address":"36SjucKqQpQSvsak9A7h6qzFjrVXpRNZhE","memo":""}}
      data = self.safe_value(response, 'data', {})
      address = self.safe_string(data, 'address')
      # BCH/BSV is returned with a "bitcoincash:" prefix, which we cut off here and only keep the address
      if address != nil
        address = address.gsub('bitcoincash:', '')
      end
      tag = self.safe_string(data, 'memo')
      self.check_address(address)
      return {
        'info' => response,
        'currency' => code,
        'address' => address,
        'tag' => tag
      }
    end

    def fetch_order_book(symbol, limit = nil, params = {})
      self.load_markets
      marketId = self.market_id(symbol)
      request = self.shallow_extend({ 'symbol' => marketId, 'level' => 2 }, params)
      response = self.publicGetMarketOrderbookLevelLevel(request)
      #
      # { sequence => '1547731421688',
      #   asks => [['5c419328ef83c75456bd615c', '0.9', '0.09'], ...],
      #   bids => [['5c419328ef83c75456bd615c', '0.9', '0.09'], ...] }
      #
      data = response['data']
      timestamp = self.safe_integer(data, 'sequence')
      # level can be a string such as 2_20 or 2_100
      levelString = self.safe_string(request, 'level')
      levelParts = levelString.split('_')
      level = parse_int(levelParts[0])
      return self.parse_order_book(data, timestamp, 'bids', 'asks', level - 2, level - 1)
    end

    def create_order(symbol, type, side, amount, price = nil, params = {})
      self.load_markets
      marketId = self.market_id(symbol)
      # required param, cannot be used twice
      clientOid = self.uuid
      request = {
        'clientOid' => clientOid,
        'side' => side,
        'size' => self.amount_to_precision(symbol, amount),
        'symbol' => marketId,
        'type' => type
      }
      if type != 'market'
        request['price'] = self.price_to_precision(symbol, price)
      end
      response = self.privatePostOrders(self.shallow_extend(request, params))
      responseData = response['data']
      return {
        'id' => responseData['orderId'],
        'symbol' => symbol,
        'type' => type,
        'side' => side,
        'status' => 'open',
        'clientOid' => clientOid,
        'info' => responseData
      }
    end

    def cancel_order(id, symbol = nil, params = {})
      request = { 'orderId' => id }
      response = self.privateDeleteOrdersOrderId(self.shallow_extend(request, params))
      return response
    end

