      request = {
        'status' => status
      }
      market = nil
      if symbol != nil
        market = self.market(symbol)
        request['symbol'] = market['id']
      end
      if since != nil
        request['startAt'] = since
      end
      if limit != nil
        request['pageSize'] = limit
      end
      response = self.privateGetOrders(self.shallow_extend(request, params))
      #
      #     {
      #         code => '200000',
      #         data => {
      #             "currentPage" => 1,
      #             "pageSize" => 1,
      #             "totalNum" => 153408,
