    def parse_order(order, market = nil)
      #
      # fetchOpenOrders, fetchClosedOrders
      #
      #     {
      #         "id" => "5c35c02703aa673ceec2a168",   #orderid
      #         "symbol" => "BTC-USDT",   #symbol
      #         "opType" => "DEAL",      # operation type,deal is pending order,cancel is cancel order
      #         "type" => "limit",       # order type,e.g. limit,markrt,stop_limit.
      #         "side" => "buy",         # transaction direction,include buy and sell
      #         "price" => "10",         # order price
      #         "size" => "2",           # order quantity
      #         "funds" => "0",          # order funds
      #         "dealFunds" => "0.166",  # deal funds
      #         "dealSize" => "2",       # deal quantity
      #         "fee" => "0",            # fee
      #         "feeCurrency" => "USDT", # charge fee currency
      #         "stp" => "",             # self trade prevention,include CN,CO,DC,CB
      #         "stop" => "",            # stop type
      #         "stopTriggered" => false,  # stop order is triggered
      #         "stopPrice" => "0",      # stop price
      #         "timeInForce" => "GTC",  # time InForce,include GTC,GTT,IOC,FOK
      #         "postOnly" => false,     # postOnly
      #         "hidden" => false,       # hidden order
      #         "iceberg" => false,      # iceberg order
      #         "visibleSize" => "0",    # display quantity for iceberg order
      #         "cancelAfter" => 0,      # cancel orders time，requires timeInForce to be GTT
      #         "channel" => "IOS",      # order source
      #         "clientOid" => "",       # user-entered order unique mark
      #         "remark" => "",          # remark
      #         "tags" => "",            # tag order source
      #         "isActive" => false,     # status before unfilled or uncancelled
      #         "cancelExist" => false,   # order cancellation transaction record
      #         "createdAt" => 1547026471000  # time
      #     }
      #
      symbol = nil
      marketId = self.safe_string(order, 'symbol')
      if marketId != nil
        if self.markets_by_id.include?(marketId)
          market = self.markets_by_id[marketId]
          symbol = market['symbol']
        else
          baseId, quoteId = marketId.split('-')
          base = self.common_currency_code(baseId)
          quote = self.common_currency_code(quoteId)
          symbol = base + '/' + quote
        end
        market = self.safe_value(self.markets_by_id, marketId)
      end
      if symbol.nil?
        if market != nil
          symbol = market['symbol']
        end
      end
      orderId = self.safe_string(order, 'id')
      type = self.safe_string(order, 'type')
      timestamp = self.safe_integer(order, 'createdAt')
      datetime = self.iso8601(timestamp)
      price = self.safe_float(order, 'price')
      side = self.safe_string(order, 'side')
      feeCurrencyId = self.safe_string(order, 'feeCurrency')
      feeCurrency = self.common_currency_code(feeCurrencyId)
      feeCost = self.safe_float(order, 'fee')
      amount = self.safe_float(order, 'size')
      filled = self.safe_float(order, 'dealSize')
      cost = self.safe_float(order, 'dealFunds')
      remaining = amount - filled
      # bool
      status = order['isActive'] ? 'open' : 'closed'
      fee = {
        'currency' => feeCurrency,
        'cost' => feeCost
      }
      if type == 'market'
        if price == 0.0
          if (cost != nil) && (filled != nil)
            if (cost > 0) && (filled > 0)
              price = cost / filled
            end
          end
        end
      end
      return {
        'id' => orderId,
        'symbol' => symbol,
        'type' => type,
        'side' => side,
        'amount' => amount,
        'price' => price,
        'cost' => cost,
        'filled' => filled,
        'remaining' => remaining,
        'timestamp' => timestamp,
        'datetime' => datetime,
        'fee' => fee,
        'status' => status,
        'info' => order
      }
    end

    def fetch_my_trades(symbol = nil, since = nil, limit = nil, params = {})
      self.load_markets
      request = {}
      market = nil
      if symbol != nil
        market = self.market(symbol)
        request['symbol'] = market['id']
      end
      if limit != nil
        request['pageSize'] = limit
      end
      method = 'privateGetFills'
      if since != nil
        # if since is earlier than 2019-02-18T00:00:00Z
        if since < 1550448000000
          request['startAt'] = parse_int(since / 1000)
          # despite that self endpoint is called `HistOrders`
          # it returns historical trades instead of orders
          method = 'privateGetHistOrders'
        else
          request['startAt'] = since
        end
      end
      response = self.send_wrapper(method, self.shallow_extend(request, params))
      #
      #     {
      #         "currentPage" => 1,
      #         "pageSize" => 50,
      #         "totalNum" => 1,
      #         "totalPage" => 1,
      #         "items" => [
      #             {
      #                 "symbol":"BTC-USDT",       # symbol
      #                 "tradeId":"5c35c02709e4f67d5266954e",        # trade id
      #                 "orderId":"5c35c02703aa673ceec2a168",        # order id
      #                 "counterOrderId":"5c1ab46003aa676e487fa8e3", # counter order id
      #                 "side":"buy",              # transaction direction,include buy and sell
      #                 "liquidity":"taker",       # include taker and maker
      #                 "forceTaker":true,         # forced to become taker
      #                 "price":"0.083",           # order price
      #                 "size":"0.8424304",        # order quantity
      #                 "funds":"0.0699217232",    # order funds
      #                 "fee":"0",                 # fee
      #                 "feeRate":"0",             # fee rate
      #                 "feeCurrency":"USDT",      # charge fee currency
      #                 "stop":"",                 # stop type
      #                 "type":"limit",            # order type, e.g. limit, market, stop_limit.
      #                 "createdAt":1547026472000  # time
      #             },
      #             #------------------------------------------------------
      #             # v1(historical) trade response structure
      #             {
      #                 "symbol" => "SNOV-ETH",
      #                 "dealPrice" => "0.0000246",
      #                 "dealValue" => "0.018942",
      #                 "amount" => "770",
      #                 "fee" => "0.00001137",
      #                 "side" => "sell",
      #                 "createdAt" => 1540080199
      #                 "id":"5c4d389e4c8c60413f78e2e5",
      #             }
      #         ]
      #     }
      #
      data = self.safe_value(response, 'data', {})
      trades = self.safe_value(data, 'items', [])
      return self.parse_trades(trades, market, since, limit)
    end

