    def parse_trade(trade, market = nil)
      #
      # fetchTrades(public)
      #
      #     {
      #         "sequence" => "1548764654235",
      #         "side" => "sell",
      #         "size":"0.6841354",
      #         "price":"0.03202",
      #         "time":1548848575203567174
      #     }
      #
      # fetchMyTrades(private) v2
      #
      #     {
      #         "symbol":"BTC-USDT",
      #         "tradeId":"5c35c02709e4f67d5266954e",
      #         "orderId":"5c35c02703aa673ceec2a168",
      #         "counterOrderId":"5c1ab46003aa676e487fa8e3",
      #         "side":"buy",
      #         "liquidity":"taker",
      #         "forceTaker":true,
      #         "price":"0.083",
      #         "size":"0.8424304",
      #         "funds":"0.0699217232",
      #         "fee":"0",
      #         "feeRate":"0",
      #         "feeCurrency":"USDT",
      #         "stop":"",
      #         "type":"limit",
      #         "createdAt":1547026472000
      #     }
      #
      # fetchMyTrades(private) v1(historical)
      #
      #     {
      #         "symbol" => "SNOV-ETH",
      #         "dealPrice" => "0.0000246",
      #         "dealValue" => "0.018942",
      #         "amount" => "770",
      #         "fee" => "0.00001137",
      #         "side" => "sell",
      #         "createdAt" => 1540080199
      #         "id":"5c4d389e4c8c60413f78e2e5",
      #     }
      #
      symbol = nil
      marketId = self.safe_string(trade, 'symbol')
      if marketId != nil
        if self.markets_by_id.include?(marketId)
          market = self.markets_by_id[marketId]
          symbol = market['symbol']
        else
          baseId, quoteId = marketId.split('-')
          base = self.common_currency_code(baseId)
          quote = self.common_currency_code(quoteId)
          symbol = base + '/' + quote
        end
      end
      if symbol.nil?
        if market != nil
          symbol = market['symbol']
        end
      end
      id = self.safe_string_2(trade, 'tradeId', 'id')
      if id != nil
        id = id.to_s
      end
      orderId = self.safe_string(trade, 'orderId')
      takerOrMaker = self.safe_string(trade, 'liquidity')
      amount = self.safe_float_2(trade, 'size', 'amount')
      timestamp = self.safe_integer(trade, 'time')
      if timestamp != nil
        timestamp = parse_int(timestamp / 1000000)
      else
        timestamp = self.safe_integer(trade, 'createdAt')
        # if it's a historical v1 trade, the exchange returns timestamp in seconds
        if takerOrMaker.nil? && timestamp != nil
          timestamp = timestamp * 1000
        end
      end
      price = self.safe_float_2(trade, 'price', 'dealPrice')
      side = self.safe_string(trade, 'side')
      fee = nil
      feeCost = self.safe_float(trade, 'fee')
      if feeCost != nil
        feeCurrencyId = self.safe_string(trade, 'feeCurrency')
        feeCurrency = self.common_currency_code(feeCurrencyId)
        if feeCurrency.nil?
          if market != nil
            feeCurrency = (side == 'sell') ? market['quote'] : market['base']
          end
        end
        fee = {
          'cost' => feeCost,
          'currency' => feeCurrency,
          'rate' => self.safe_float(trade, 'feeRate')
        }
      end
      type = self.safe_string(trade, 'type')
      cost = self.safe_float_2(trade, 'funds', 'dealValue')
      if cost.nil?
        if amount != nil
          if price != nil
            cost = amount * price
          end
        end
      end
      return {
        'info' => trade,
        'id' => id,
        'order' => orderId,
        'timestamp' => timestamp,
        'datetime' => self.iso8601(timestamp),
        'symbol' => symbol,
        'type' => type,
        'takerOrMaker' => takerOrMaker,
        'side' => side,
        'price' => price,
        'amount' => amount,
        'cost' => cost,
        'fee' => fee
      }
    end

    def withdraw(code, amount, address, tag = nil, params = {})
      self.load_markets
      self.check_address(address)
      currency = self.currencyId(code)
      request = {
        'currency' => currency,
        'address' => address,
        'amount' => amount
      }
      if tag != nil
        request['memo'] = tag
      end
      response = self.privatePostWithdrawals(self.shallow_extend(request, params))
      #
      # { "withdrawalId" => "5bffb63303aa675e8bbe18f9" }
      #
      return {
        'id' => self.safe_string(response, 'withdrawalId'),
        'info' => response
      }
    end

    def parse_transaction_status(status)
      statuses = {
        'SUCCESS' => 'ok',
        'PROCESSING' => 'ok',
        'FAILURE' => 'failed'
      }
      return self.safe_string(statuses, status)
    end

    def parse_transaction(transaction, currency = nil)
      #
      # fetchDeposits
      #
      #     {
      #         "address" => "0x5f047b29041bcfdbf0e4478cdfa753a336ba6989",
      #         "memo" => "5c247c8a03aa677cea2a251d",
      #         "amount" => 1,
      #         "fee" => 0.0001,
      #         "currency" => "KCS",
      #         "isInner" => false,
      #         "walletTxId" => "5bbb57386d99522d9f954c5a@test004",
      #         "status" => "SUCCESS",
      #         "createdAt" => 1544178843000,
      #         "updatedAt" => 1544178891000
      #     }
      #
      # fetchWithdrawals
      #
      #     {
      #         "id" => "5c2dc64e03aa675aa263f1ac",
      #         "address" => "0x5bedb060b8eb8d823e2414d82acce78d38be7fe9",
      #         "memo" => "",
      #         "currency" => "ETH",
      #         "amount" => 1.0000000,
      #         "fee" => 0.0100000,
      #         "walletTxId" => "3e2414d82acce78d38be7fe9",
      #         "isInner" => false,
      #         "status" => "FAILURE",
      #         "createdAt" => 1546503758000,
      #         "updatedAt" => 1546504603000
      #     }
      #
      code = nil
      currencyId = self.safe_string(transaction, 'currency')
      currency = self.safe_value(self.currencies_by_id, currencyId)
      if currency != nil
        code = currency['code']
      else
        code = self.common_currency_code(currencyId)
      end
      address = self.safe_string(transaction, 'address')
      amount = self.safe_float(transaction, 'amount')
      txid = self.safe_string(transaction, 'walletTxId')
      if txid != nil
        txidParts = txid.split('@')
        numTxidParts = txidParts.length
        if numTxidParts > 1
          if address.nil?
            if txidParts[1].length > 1
              address = txidParts[1]
            end
          end
        end
        txid = txidParts[0]
      end
      type = txid.nil? ? 'withdrawal' : 'deposit'
      rawStatus = self.safe_string(transaction, 'status')
      status = self.parse_transaction_status(rawStatus)
      fee = nil
      feeCost = self.safe_float(transaction, 'fee')
      if feeCost != nil
        rate = nil
        if amount != nil
          rate = feeCost / amount
        end
        fee = {
          'cost' => feeCost,
          'rate' => rate,
          'currency' => code
        }
      end
      tag = self.safe_string(transaction, 'memo')
      timestamp = self.safe_integer_2(transaction, 'createdAt', 'createAt')
      id = self.safe_string(transaction, 'id')
      updated = self.safe_integer(transaction, 'updatedAt')
      isV1 = !(transaction.include?('createdAt'))
      # if it's a v1 structure
      if isV1
        type = (transaction.include?('address')) ? 'withdrawal' : 'deposit'
        if timestamp != nil
          timestamp = timestamp * 1000
        end
        if updated != nil
          updated = updated * 1000
        end
      end
      return {
        'id' => id,
        'address' => address,
        'tag' => tag,
        'currency' => code,
        'amount' => amount,
        'txid' => txid,
        'type' => type,
        'status' => status,
        'fee' => fee,
        'timestamp' => timestamp,
        'datetime' => self.iso8601(timestamp),
        'updated' => updated,
        'info' => transaction
      }
    end

