    def calculate_fee(symbol, type, side, amount, price, takerOrMaker = 'taker', params = {})
      market = self.markets[symbol]
      key = 'quote'
      rate = market[takerOrMaker]
      cost = self.cost_to_precision(symbol, amount * rate.to_f)
      if side == 'sell'
        cost *= price
      else
        key = 'base'
      end
      return {
        'type' => takerOrMaker,
        'currency' => market[key],
        'rate' => rate,
        'cost' => self.fee_to_precision(symbol, cost.to_f)
      }
    end

    def parse_ohlcv(ohlcv, market = nil, timeframe = '5m', since = nil, limit = nil)
      return [
        ohlcv['date'] * 1000,
        ohlcv['open'],
        ohlcv['high'],
        ohlcv['low'],
        ohlcv['close'],
        ohlcv['quoteVolume']
      ]
    end

    def fetch_ohlcv(symbol, timeframe = '5m', since = nil, limit = nil, params = {})
      self.load_markets
      market = self.market(symbol)
      if since.nil?
        since = 0
      end
      request = {
        'currencyPair' => market['id'],
        'period' => self.timeframes[timeframe],
        'start' => (since / 1000).to_i
      }
      if limit != nil
        request['end'] = self.sum(request['start'], limit * self.timeframes[timeframe])
      else
        request['end'] = self.sum(self.seconds, 1)
      end
      response = self.publicGetReturnChartData(shallow_extend(request, params))
      return self.parse_ohlcvs(response, market, timeframe, since, limit)
    end

    def fetch_markets(params = {})
      markets = self.publicGetReturnTicker
      keys = markets.keys
      result = []
      for p in (0...keys.length)
        id = keys[p]
        market = markets[id]
        quoteId, baseId = id.split('_')
        base = self.common_currency_code(baseId)
        quote = self.common_currency_code(quoteId)
        symbol = base + '/' + quote
        limits = shallow_extend(self.limits, {
          'cost' => {
            'min' => self.safe_value(self.options['limits']['cost']['min'], quote)
          }
        })
        result.push(shallow_extend(self.fees['trading'], {
          'id' => id,
          'symbol' => symbol,
          'baseId' => baseId,
          'quoteId' => quoteId,
          'base' => base,
          'quote' => quote,
          'active' => market['isFrozen'] != '1',
          'limits' => limits,
          'info' => market
        }))
      end
      return result
    end

    def fetch_balance(params = {})
      self.load_markets
      balances = self.privatePostReturnCompleteBalances(shallow_extend({
        'account' => 'all'
      }, params))
      result = { 'info' => balances }
      currencies = balances.keys
      for c in (0...currencies.length)
        id = currencies[c]
        balance = balances[id]
        currency = self.common_currency_code(id)
        account = {
          'free' => balance['available'].to_f,
          'used' => balance['onOrders'].to_f,
          'total' => 0.0
        }
        account['total'] = self.sum(account['free'], account['used'])
        result[currency] = account
      end
      return self.parse_balance(result)
    end

