    def parse_trade(trade, market = nil)
      #
      # fetchMyTrades(symbol defined, specific market)
      #
      #     {
      #         globalTradeID => 394698946,
      #         tradeID => 45210255,
      #         date => '2018-10-23 17:28:55',
      #         type => 'sell',
      #         rate => '0.03114126',
      #         amount => '0.00018753',
      #         total => '0.00000583'
      #     }
      #
      # fetchMyTrades(symbol nil, all markets)
      #
      #     {
      #         globalTradeID => 394131412,
      #         tradeID => '5455033',
      #         date => '2018-10-16 18:05:17',
      #         rate => '0.06935244',
      #         amount => '1.40308443',
      #         total => '0.09730732',
      #         fee => '0.00100000',
      #         orderNumber => '104768235081',
      #         type => 'sell',
      #         category => 'exchange'
      #     }
      #
      id = self.safe_string(trade, 'globalTradeID')
      orderId = self.safe_string(trade, 'orderNumber')
      timestamp = self.parse8601(self.safe_string(trade, 'date'))
      symbol = nil
      base = nil
      quote = nil
      if (!market) && (trade.include?('currencyPair'))
        currencyPair = trade['currencyPair']
        if self.markets_by_id.include?(currencyPair)
          market = self.markets_by_id[currencyPair]
        else
          parts = currencyPair.split('_')
          quote = parts[0]
          base = parts[1]
          symbol = base + '/' + quote
        end
      end
      if market != nil
        symbol = market['symbol']
        base = market['base']
        quote = market['quote']
      end
      side = self.safe_string(trade, 'type')
      fee = nil
      price = self.safe_string(trade, 'rate')
      cost = self.safe_float(trade, 'total')
      amount = self.safe_float(trade, 'amount')
      if trade.include?('fee')
        rate = self.safe_float(trade, 'fee')
        feeCost = nil
        currency = nil
        if side == 'buy'
          currency = base
          feeCost = amount * rate
        else
          currency = quote
          if cost != nil
            feeCost = cost * rate
          end
        end
        fee = {
          'type' => nil,
          'rate' => rate,
          'cost' => feeCost,
          'currency' => currency
        }
      end
      return {
        'info' => trade,
        'timestamp' => timestamp,
        'datetime' => self.iso8601(timestamp),
        'symbol' => symbol,
        'id' => id,
        'order' => orderId,
        'type' => 'limit',
        'side' => side,
        'price' => price,
        'amount' => amount,
        'cost' => cost,
        'fee' => fee
      }
    end

    def fetch_trades(symbol, since = nil, limit = nil, params = {})
      self.load_markets
      market = self.market(symbol)
      request = {
        'currencyPair' => market['id']
      }
      if since != nil
        request['start'] = (since / 1000).to_i
        request['end'] = self.seconds # last 50000 trades by default
      end
      trades = self.publicGetReturnTradeHistory(shallow_extend(request, params))
      return self.parse_trades(trades, market, since, limit)
    end

    def fetch_my_trades(symbol = nil, since = nil, limit = nil, params = {})
      self.load_markets
      market = nil
      if symbol != nil
        market = self.market(symbol)
      end
      pair = market ? market['id'] : 'all'
      request = { 'currencyPair' => pair }
      if since != nil
        request['start'] = (since / 1000).to_i
        request['end'] = self.seconds + 1 # adding 1 is a fix for #3411
      end
      # limit is disabled(does not really work as expected)
      if limit != nil
        request['limit'] = (limit).to_i
      end
      response = self.privatePostReturnTradeHistory(shallow_extend(request, params))
      #
      # specific market(symbol defined)
      #
      #     [
      #         {
      #             globalTradeID => 394700861,
      #             tradeID => 45210354,
      #             date => '2018-10-23 18:01:58',
      #             type => 'buy',
      #             rate => '0.03117266',
      #             amount => '0.00000652',
      #             total => '0.00000020'
      #         },
      #         {
      #             globalTradeID => 394698946,
      #             tradeID => 45210255,
      #             date => '2018-10-23 17:28:55',
      #             type => 'sell',
      #             rate => '0.03114126',
      #             amount => '0.00018753',
      #             total => '0.00000583'
      #         }
      #     ]
      #
      # all markets(symbol nil)
      #
      #     {
      #         BTC_BCH => [{
      #             globalTradeID => 394131412,
      #             tradeID => '5455033',
      #             date => '2018-10-16 18:05:17',
      #             rate => '0.06935244',
      #             amount => '1.40308443',
      #             total => '0.09730732',
      #             fee => '0.00100000',
      #             orderNumber => '104768235081',
      #             type => 'sell',
      #             category => 'exchange'
      #         }, {
      #             globalTradeID => 394126818,
      #             tradeID => '5455007',
      #             date => '2018-10-16 16:55:34',
      #             rate => '0.06935244',
      #             amount => '0.00155709',
      #             total => '0.00010798',
      #             fee => '0.00200000',
      #             orderNumber => '104768179137',
      #             type => 'sell',
      #             category => 'exchange'
      #         }],
      #     }
      #
      result = []
      if market != nil
        result = self.parse_trades(response, market)
      else
        if response
          ids = response.keys
          for i in (0...ids.length)
            id = ids[i]
            market = nil
            if self.markets_by_id.include?(id)
              market = self.markets_by_id[id]
              trades = self.parse_trades(response[id], market)
              for j in (0...trades.length)
                result.push(trades[j])
              end
            else
              quoteId, baseId = id.split('_')
              base = self.common_currency_code(baseId)
              quote = self.common_currency_code(quoteId)
              symbol = base + '/' + quote
              trades = response[id]
              for j in (0...trades.length)
                result.push(shallow_extend(self.parse_trade(trades[j]), {
                  'symbol' => symbol
                }))
              end
            end
          end
        end
      end
      return self.filter_by_since_limit(result, since, limit)
    end

    def parse_order_status(status)
      statuses = {
        'Open' => 'open',
        'Partially filled' => 'open'
      }
      return self.safe_string(statuses, status, status)
    end

    def parse_order(order, market = nil)
      #
      # fetchOpenOrder
      #
      #     {
      #         status => 'Open',
      #         rate => '0.40000000',
      #         amount => '1.00000000',
      #         currencyPair => 'BTC_ETH',
      #         date => '2018-10-17 17:04:50',
      #         total => '0.40000000',
      #         type => 'buy',
      #         startingAmount => '1.00000',
      #     }
      #
      # fetchOpenOrders
      #
      #     {
      #         orderNumber => '514514894224',
      #         type => 'buy',
      #         rate => '0.00001000',
      #         startingAmount => '100.00000000',
      #         amount => '100.00000000',
      #         total => '0.00100000',
      #         date => '2018-10-23 17:38:53',
      #         margin => 0,
      #     }
      #
      timestamp = self.safe_integer(order, 'timestamp')
      if !timestamp
        timestamp = self.parse8601(order['date'])
      end
      trades = nil
      if order.include?('resultingTrades')
        trades = self.parse_trades(order['resultingTrades'], market)
      end
      symbol = nil
      marketId = self.safe_string(order, 'currencyPair')
      market = self.safe_value(self.markets_by_id, marketId, market)
      if market != nil
        symbol = market['symbol']
      end
      price = self.safe_float_2(order, 'price', 'rate')
      remaining = self.safe_float(order, 'amount')
      amount = self.safe_float(order, 'startingAmount', remaining)
      filled = nil
      cost = 0
      if amount != nil
        if remaining != nil
          filled = amount - remaining
          if price != nil
            cost = filled * price
          end
        end
      end
      if filled.nil?
        if trades != nil
          filled = 0
          cost = 0
          for i in (0...trades.length)
            trade = trades[i]
            tradeAmount = trade['amount']
            tradePrice = trade['price']
            filled = self.sum(filled, tradeAmount)
            cost += tradePrice * tradeAmount
          end
        end
      end
      status = self.parse_order_status(self.safe_string(order, 'status'))
      type = self.safe_string(order, 'type')
      side = self.safe_string(order, 'side', type)
      if type == side
        type = nil
      end
      return {
        'info' => order,
        'id' => order['orderNumber'],
        'timestamp' => timestamp,
        'datetime' => self.iso8601(timestamp),
        'lastTradeTimestamp' => nil,
        'status' => status,
        'symbol' => symbol,
        'type' => type,
        'side' => side,
        'price' => price,
        'cost' => cost,
        'amount' => amount,
        'filled' => filled,
        'remaining' => remaining,
        'trades' => trades,
        'fee' => nil
      }
    end

    def parse_open_orders(orders, market, result)
      for i in (0...orders.length)
        order = orders[i]
        extended = shallow_extend(order, {
          'status' => 'open',
          'type' => 'limit',
          'side' => order['type'],
          'price' => order['rate']
        })
        result.push(self.parse_order(extended, market))
      end
      return result
    end

    def fetch_orders(symbol = nil, since = nil, limit = nil, params = {})
      self.load_markets
      market = nil
      if symbol != nil
        market = self.market(symbol)
      end
      pair = market ? market['id'] : 'all'
      response = self.privatePostReturnOpenOrders(shallow_extend({
        'currencyPair' => pair
      }))
      openOrders = []
      if market != nil
        openOrders = self.parse_open_orders(response, market, openOrders)
      else
        marketIds = response.keys
        for i in (0...marketIds.length)
          marketId = marketIds[i]
          orders = response[marketId]
          m = self.markets_by_id[marketId]
          openOrders = self.parse_open_orders(orders, m, openOrders)
        end
      end
      for j in (0...openOrders.length)
        self.orders[openOrders[j]['id']] = openOrders[j]
      end
      openOrdersIndexedById = self.index_by(openOrders, 'id')
      cachedOrderIds = self.orders.keys
      result = []
      for k in (0...cachedOrderIds.length)
        id = cachedOrderIds[k]
        if openOrdersIndexedById.include?(id)
          self.orders[id] = shallow_extend(self.orders[id], openOrdersIndexedById[id])
        else
          order = self.orders[id]
          if order['status'] == 'open'
            order = shallow_extend(order, {
              'status' => 'closed',
              'cost' => nil,
              'filled' => order['amount'],
              'remaining' => 0.0
            })
            if order['cost'].nil?
              if order['filled'] != nil
                order['cost'] = order['filled'] * order['price']
              end
            end
            self.orders[id] = order
          end
        end
        order = self.orders[id]
        if market != nil
          if order['symbol'] == symbol
            result.push(order)
          end
        else
          result.push(order)
        end
      end
      return self.filter_by_since_limit(result, since, limit)
    end

    def fetch_order(id, symbol = nil, params = {})
      since = self.safe_value(params, 'since')
      limit = self.safe_value(params, 'limit')
      request = self.omit(params, ['since', 'limit'])
      orders = self.fetch_orders(symbol, since, limit, request)
      for i in (0...orders.length)
        if orders[i]['id'] == id
          return orders[i]
        end
      end
      raise(OrderNotCached, self.id + ' order id ' + id.to_s + ' is not in "open" state and not found in cache')
    end

    def filter_orders_by_status(orders, status)
      result = []
      for i in (0...orders.length)
        if orders[i]['status'] == status
          result.push(orders[i])
        end
      end
      return result
    end

    def fetch_open_orders(symbol = nil, since = nil, limit = nil, params = {})
      orders = self.fetch_orders(symbol, since, limit, params)
      return self.filter_orders_by_status(orders, 'open')
    end

    def fetch_closed_orders(symbol = nil, since = nil, limit = nil, params = {})
      orders = self.fetch_orders(symbol, since, limit, params)
      return self.filter_orders_by_status(orders, 'closed')
    end

    def create_order(symbol, type, side, amount, price = nil, params = {})
      if type == 'market'
        raise(ExchangeError, self.id + ' allows limit orders only')
      end
      self.load_markets
      method = 'privatePost' + self.capitalize(side)
      market = self.market(symbol)
      request = {
        'currencyPair' => market['id'],
        'rate' => self.price_to_precision(symbol, price),
        'amount' => self.amount_to_precision(symbol, amount)
      }
      response = self.send_wrapper(method, shallow_extend(request, params))
      timestamp = self.milliseconds
      order = self.parse_order(shallow_extend({
        'timestamp' => timestamp,
        'status' => 'open',
        'type' => type,
        'side' => side,
        'price' => price,
        'amount' => amount
      }, response), market)
      id = order['id']
      self.orders[id] = order
      return shallow_extend({ 'info' => response }, order)
    end

    def edit_order(id, symbol, type, side, amount, price = nil, params = {})
      self.load_markets
      price = price.to_f
      request = {
        'orderNumber' => id,
        'rate' => self.price_to_precision(symbol, price)
      }
      if amount != nil
        request['amount'] = self.amount_to_precision(symbol, amount)
      end
      response = self.privatePostMoveOrder(shallow_extend(request, params))
      result = nil
      if self.orders.include?(id)
        self.orders[id]['status'] = 'canceled'
        newid = response['orderNumber']
        self.orders[newid] = shallow_extend(self.orders[id], {
          'id' => newid,
          'price' => price,
          'status' => 'open'
        })
        if amount != nil
          self.orders[newid]['amount'] = amount
        end
        result = shallow_extend(self.orders[newid], { 'info' => response })
      else
        market = nil
        if symbol != nil
          market = self.market(symbol)
        end
        result = self.parse_order(response, market)
        self.orders[result['id']] = result
      end
      return result
    end

    def cancel_order(id, symbol = nil, params = {})
      self.load_markets
      response = nil
      begin
        response = self.privatePostCancelOrder(shallow_extend({
          'orderNumber' => id
        }, params))
      rescue BaseError => e
        if e.is_a?(CancelPending)
          # A request to cancel the order has been sent already.
          # If we then attempt to cancel the order the second time
          # before the first request is processed the exchange will
          # raise a CancelPending exception. Poloniex won't show the
          # order in the list of active(open) orders and the cached
          # order will be marked as 'closed'(see #1801 for details).
          # To avoid that we proactively mark the order as 'canceled'
          # here. If for some reason the order does not get canceled
          # and still appears in the active list then the order cache
          # will eventually get back in sync on a call to `fetchOrder`.
          if self.orders.include?(id)
            self.orders[id]['status'] = 'canceled'
          end
        end
        raise e
      end
      if self.orders.include?(id)
        self.orders[id]['status'] = 'canceled'
      end
      return response
    end

    def fetch_open_order(id, symbol = nil, params = {})
      self.load_markets
      id = id.to_s
      response = self.privatePostReturnOrderStatus(shallow_extend({
        'orderNumber' => id
      }, params))
      #
      #     {
      #         success => 1,
      #         result => {
      #             '6071071' => {
      #                 status => 'Open',
      #                 rate => '0.40000000',
      #                 amount => '1.00000000',
      #                 currencyPair => 'BTC_ETH',
      #                 date => '2018-10-17 17:04:50',
      #                 total => '0.40000000',
      #                 type => 'buy',
      #                 startingAmount => '1.00000',
      #             },
      #         },
      #     }
      #
      result = self.safe_value(response['result'], id)
      if result.nil?
        raise(OrderNotFound, self.id + ' order id ' + id + ' not found')
      end
      order = self.parse_order(result)
      order['id'] = id
      self.orders[id] = order
      return order
    end

    def fetch_order_status(id, symbol = nil, params = {})
      self.load_markets
      orders = self.fetch_open_orders(symbol, nil, nil, params)
      indexed = self.index_by(orders, 'id')
      return(indexed.include?(id)) ? 'open' : 'closed'
    end

    def fetch_order_trades(id, symbol = nil, since = nil, limit = nil, params = {})
      self.load_markets
      trades = self.privatePostReturnOrderTrades(shallow_extend({
        'orderNumber' => id
      }, params))
      return self.parse_trades(trades)
    end

    def create_deposit_address(code, params = {})
      self.load_markets
      currency = self.currency(code)
      response = self.privatePostGenerateNewAddress({
        'currency' => currency['id']
      })
      address = nil
      tag = nil
      if response['success'] == 1
        address = self.safe_string(response, 'response')
      end
      self.check_address(address)
      depositAddress = self.safe_string(currency['info'], 'depositAddress')
      if depositAddress != nil
        tag = address
        address = depositAddress
      end
      return {
        'currency' => code,
        'address' => address,
        'tag' => tag,
        'info' => response
      }
    end

    def fetch_deposit_address(code, params = {})
      self.load_markets
      currency = self.currency(code)
      response = self.privatePostReturnDepositAddresses
      currencyId = currency['id']
      address = self.safe_string(response, currencyId)
      tag = nil
      self.check_address(address)
      depositAddress = self.safe_string(currency['info'], 'depositAddress')
      if depositAddress != nil
        tag = address
        address = depositAddress
      end
      return {
        'currency' => code,
        'address' => address,
        'tag' => tag,
        'info' => response
      }
    end

    def withdraw(code, amount, address, tag = nil, params = {})
      self.check_address(address)
      self.load_markets
      currency = self.currency(code)
      request = {
        'currency' => currency['id'],
        'amount' => amount,
        'address' => address
      }
      if tag
        request['paymentId'] = tag
      end
      result = self.privatePostWithdraw(shallow_extend(request, params))
      return {
        'info' => result,
        'id' => result['response']
      }
    end

    def fetch_transactions_helper(code = nil, since = nil, limit = nil, params = {})
      self.load_markets
      year = 31104000 # 60 * 60 * 24 * 30 * 12 = one year of history, why not
      now = self.seconds
      start = (since != nil) ?(since / 1000).to_i : now - 10 * year
      request = {
        'start' => start, # UNIX timestamp, required
        'end' => now, # UNIX timestamp, required
      }
      if limit != nil
        request['limit'] = limit
      end
      response = self.privatePostReturnDepositsWithdrawals(shallow_extend(request, params))
      #
      #     {    deposits => [{      currency => "BTC",
      #                              address => "1MEtiqJWru53FhhHrfJPPvd2tC3TPDVcmW",
