            builder.Append("ListStatusType=");
            builder.Append(this.ListStatusType);
            builder.Append('|');
            builder.Append("ListOrderStatus=");
            builder.Append(this.ListOrderStatus);
            builder.Append('|');
            builder.Append("TransactionTime=");
            builder.Append(this.TransactionTime);
            builder.Append('|');
            builder.Append("Orders=[");
            var orders = this.Orders;
            if (orders.Count > 0)
            {
                var first = true;
                while (orders.HasNext)
                {
                    if (!first)
                    {
                        builder.Append(',');
                    }
                    first = false;
                    orders.Next().BuildString(builder);
                }
            }
            builder.Append("]");
            builder.Append('|');
            builder.Append("ListClientOrderId=");
            builder.Append('\'').Append(GetListClientOrderId()).Append('\'');
            builder.Append('|');
            builder.Append("Symbol=");
            builder.Append('\'').Append(GetSymbol()).Append('\'');

            this.Limit = originalLimit;

        }
    }
}
